import { Konva } from './Global.js';
import { Util } from './Util.js';
export var DD = {
  get isDragging() {
    var flag = false;

    DD._dragElements.forEach(function (elem) {
      if (elem.dragStatus === 'dragging') {
        flag = true;
      }
    });

    return flag;
  },

  justDragged: false,

  get node() {
    var node;

    DD._dragElements.forEach(function (elem) {
      node = elem.node;
    });

    return node;
  },

  _dragElements: new Map(),
  _drag: function _drag(evt) {
    var nodesToFireEvents = [];

    DD._dragElements.forEach(function (elem, key) {
      var node = elem.node;
      var stage = node.getStage();
      stage.setPointersPositions(evt);

      if (elem.pointerId === undefined) {
        elem.pointerId = Util._getFirstPointerId(evt);
      }

      var pos = stage._changedPointerPositions.find(function (pos) {
        return pos.id === elem.pointerId;
      });

      if (!pos) {
        return;
      }

      if (elem.dragStatus !== 'dragging') {
        var dragDistance = node.dragDistance();
        var distance = Math.max(Math.abs(pos.x - elem.startPointerPos.x), Math.abs(pos.y - elem.startPointerPos.y));

        if (distance < dragDistance) {
          return;
        }

        node.startDrag({
          evt: evt
        });

        if (!node.isDragging()) {
          return;
        }
      }

      node._setDragPosition(evt, elem);

      nodesToFireEvents.push(node);
    });

    nodesToFireEvents.forEach(function (node) {
      node.fire('dragmove', {
        type: 'dragmove',
        target: node,
        evt: evt
      }, true);
    });
  },
  _endDragBefore: function _endDragBefore(evt) {
    DD._dragElements.forEach(function (elem) {
      var node = elem.node;
      var stage = node.getStage();

      if (evt) {
        stage.setPointersPositions(evt);
      }

      var pos = stage._changedPointerPositions.find(function (pos) {
        return pos.id === elem.pointerId;
      });

      if (!pos) {
        return;
      }

      if (elem.dragStatus === 'dragging' || elem.dragStatus === 'stopped') {
        DD.justDragged = true;
        Konva._mouseListenClick = false;
        Konva._touchListenClick = false;
        Konva._pointerListenClick = false;
        elem.dragStatus = 'stopped';
      }

      var drawNode = elem.node.getLayer() || elem.node instanceof Konva['Stage'] && elem.node;

      if (drawNode) {
        drawNode.batchDraw();
      }
    });
  },
  _endDragAfter: function _endDragAfter(evt) {
    DD._dragElements.forEach(function (elem, key) {
      if (elem.dragStatus === 'stopped') {
        elem.node.fire('dragend', {
          type: 'dragend',
          target: elem.node,
          evt: evt
        }, true);
      }

      if (elem.dragStatus !== 'dragging') {
        DD._dragElements.delete(key);
      }
    });
  }
};

if (Konva.isBrowser) {
  window.addEventListener('mouseup', DD._endDragBefore, true);
  window.addEventListener('touchend', DD._endDragBefore, true);
  window.addEventListener('mousemove', DD._drag);
  window.addEventListener('touchmove', DD._drag);
  window.addEventListener('mouseup', DD._endDragAfter, false);
  window.addEventListener('touchend', DD._endDragAfter, false);
}
